interface IconProps {
  color?: string;
  size?: number;
  style?: StyleProp<ViewStyle>;
}

export function SvgIcon({
  svgContent,
  color = '#000',
  size = 24,
  style
}: IconProps & { svgContent: string }) {
  const coloredSvg = svgContent.replace(/currentColor/g, color);
  return <SvgXml xml={coloredSvg} width={size} height={size} style={style} />;
}


